    protected String getMaxIndentation(final String keyword) {
        // Max Indentation may be changed here
        int maxIndent = keyword.length() + 1;
        String indent = "";
        for (int i = 0; i < maxIndent; i++) {
            indent += " ";
        }
        return indent;
    }
